package newcoding;

public class MultiplicationwithoutMultiply {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//10*5
		int number =10;
		int multply_by = 5;
		int sum=0;
		
		for(int i=1; i<=multply_by; i++) {
			sum+=number;
		}
		
		System.out.println(sum);
	}

}
